const artReducers = (
    state = { 
        arts: []
    }, action) => {
        
    switch(action.type) {
        case 'SHOW_ARTS': 
        debugger
            return {
                ...state,
                arts: action.arts
            }
        default:
            return state;
        }
    }

export default artReducers;
