import React, { Component } from 'react';
import {connect} from 'react-redux'
import {findArtById} from '../actions/artActions'

class Art extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "id": null,
            "name": "",
            "description": "",
            "artist_id": null,
        }
    }
    
    componentWillMount() {
        //findArtById(this.props.match.params.id)
        fetch('http://localhost:4000/arts/1')
            .then(response => {
                return response.json()
            }).then(responseJSON => {
                this.setState(responseJSON)
                //dispatch({ type: 'SHOW_ART', arts: responseJSON })
                //debugger
            }).catch(error => {
                debugger
                console.log(error)
            })
    }
    
    render() {
        return (
            <div>
                <div className="bandeau">Bien jou√© le sang</div>

                {this.state.name}
            </div>
        );
    }
}

const mapsStateToProps = (state) => {
    //TODO refator to collect only the reducer
    return state
}

export default connect(mapsStateToProps, {findArtById})(Art);
