import React, { Component } from 'react';

class Quizz extends Component {
    constructor(props) {
        super(props);
        
        //TODO get from db
        this.acceptedAnswers = ['boule', 'boules']

        this.state={
            answer: ""
        };

    }
    
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();

        const hey = this.acceptedAnswers.find(correctAnswer => correctAnswer === this.state.answer)
        if (!!!hey) {
            console.log("incorrect")
            document.getElementsByTagName('input')[0].style.color = 'red'
        }
        else {
            window.location.pathname = "/art/" + this.props.match.params.id
        }
    }

    render() {
        return (
            <div className="quizz">
            <div className="quizz-title ">
                <h2>
                    Je peux mesurer 2m comme 2cm<br/>
                </h2>
            </div>

            <form onSubmit={this.handleSubmit} className="anwser">
                <input name="answer" type="text" placeholder="Ma RÃ©ponse" onChange={this.handleChange}></input>
                <button>Entrez</button>
            </form>
        </div>
        );
    }
}

export default Quizz;
