import React, { Component } from 'react';
import {connect} from 'react-redux'
import {findArtById} from '../actions/artActions'

class Quizz extends Component {
    constructor(props) {
        super(props);
        
        this.state={
            answer: ""
        };
    }

    componentDidMount() {
        /*//TODO fix this
        console.log("loading")
        fetch('http://localhost:4000/arts/' + this.props.match.params.id)
            .then(response => {
                return response.json()
            }).then(responseJSON => {
                this.setState(responseJSON)
                //dispatch({ type: 'SHOW_ART', arts: responseJSON })
                this.acceptedAnswers = this.acceptedAnswers.concat(responseJSON.answers.split(', '))
            }).catch(error => {
                console.log(error)
            })
            */
            this.props.findArtById(this.props.match.params.id)
            //debugger
            
            console.log(this.props)
        }
    
    
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();

        //TODO case sensetive
        const correctAnswers = this.props.answers.concat(responseJSON.answers.split(', '))
        if (!!! this.correctAnswers.split.find(correctAnswer => correctAnswer === this.state.answer)) {
            console.log("incorrect")
            document.getElementsByTagName('input')[0].style.color = 'red'
        }
        else {
            window.location.pathname = "/art/" + this.props.match.params.id
        }
    }

    render() {
        return (
            <div className="quizz">
            <div className="quizz-title ">
                <h2>
                    {this.props.enigma}
                </h2>

            </div>

            <form onSubmit={this.handleSubmit} className="answer">
                <input name="answer" type="text" placeholder="Ma RÃ©ponse" onChange={this.handleChange}></input>
                <button>Entrez</button>
            </form>
        </div>
        );
    }
}

const mapsStateToProps = (state) => {
    //TODO refator to collect only the reducer
    return state.art
}

export default connect(mapsStateToProps, {findArtById})(Quizz);
