import React, { Component } from 'react';
import {connect} from 'react-redux'
import {findArtById} from '../actions/artActions'

import audio from '../png/272168__dethrok__cicadas.wav';

class Art extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "name": "Loading"
        }

        this.props.findArtById(this.props.match.params.id)

    }
        
    render() {
        return (
            <div>
                <div className="bandeau">Bien joué</div>
                {/*<div className="art">
                    <h2>{this.props.name}</h2>
                    <img src={this.props.image_url} alt={this.state.name}/>
                </div>

                <div className="art">
                    <audio
                        src={audio}
                        controls
                    /><br/>
                    {this.props.description}
        </div>*/}

                <div className="book">
                    <div class="book-cell">
                        <div class="book-img">
                            <img src="https://images-na.ssl-images-amazon.com/images/I/81WcnNQ-TBL.jpg" alt="" class="book-photo"></img>
                        </div>
                        <div class="book-content">
                            <div class="book-title">
                                BIG MAGIC
                            </div>
                            <div class="book-author">
                                by Elizabeth Gilbert
                            </div>

                            <div class="book-sum">
                                Readers of all ages and walks of life have drawn inspiration and empowerment from Elizabeth Gilbert’s books for years. 
                            </div>
                            <div class="book-see">
                                See The Book
                            </div>
                        </div>
                    </div>
                </div>
                
                <div className="carte">
                    <iframe src={this.props.map_url} title={this.props.name}></iframe>
                </div>
                
                
            </div>
        );
    }
}

const mapsStateToProps = (state) => {
    return state.art
}

export default connect(mapsStateToProps, {findArtById})(Art);
