import React, { Component } from 'react';
import {connect} from 'react-redux'
import {findArtById} from '../actions/artActions'

class Art extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "name": "Loading"
        }
    }
    
    componentWillMount() {
        //findArtById(this.props.match.params.id)
        fetch('http://localhost:4000/arts/' + this.props.match.params.id)
            .then(response => {
                return response.json()
            }).then(responseJSON => {
                this.setState(responseJSON)
                //dispatch({ type: 'SHOW_ART', arts: responseJSON })
                //debugger
            }).catch(error => {
                console.log(error)
            })
    }
    
    
    render() {
        debugger
        return (
            <div>
                <div className="bandeau">Bien jou√©</div>
                <div className="art">
                    <h2>{this.state.name}</h2>
                    <img src={this.state.image_url} alt={this.state.name}/>
                </div>

                <div className="art">Ici je suis le menu audio</div>
                <div className="art">{this.state.description}</div>
                <div className="carte">
                    <iframe src={this.state.map_url} ></iframe>
                </div>
                
                
            </div>
        );
    }
}

const mapsStateToProps = (state) => {
    //TODO refator to collect only the reducer
    return state
}

export default connect(mapsStateToProps, {findArtById})(Art);
