import React, { Component } from 'react';
import {connect} from 'react-redux'
//import "../Enigma.css"

import Enigma from "../components/Enigma"
import {loadArts} from "../actions/artActions"

   /*
    return (
        <div>
            <div className="bandeau">Mes Enigmes</div>

            <Enigma title="Au Pied du Mur" status="completed"/>
            <Enigma title="C'est beau la vie" path="/" status=""/>
            <Enigma title="#Jul le 100" path="/" status=""/>
            
        </div>
    );*/




class enigmaContainer extends Component {
    //belongs in Home?
    componentDidMount() {
        this.props.loadArts()
    }
    
    renderEnigmas = () => {
        // TODO: art.name => art.enigma_name
        debugger
        return this.props.arts.map(art => <Enigma key={art.id} title={art.name} status="" />)
    }

    render() {
        return (
            <div>
                {this.renderEnigmas()}
            </div>
        );
    }
}
const mapsStateToProps = (state) => {
    //TODO refator to collect only the reducer
    return state
}

export default connect(mapsStateToProps, {loadArts})(enigmaContainer);
